    [TestClass]
    class recordTypeTests
    {
        [TestMethod]
        static void testDrugRecord()
        {
            motDrugRecord r;
            motSocket p;

            try
            {
                p = new motSocket("localhost", 24042);
                r = new motDrugRecord("Add");

                // Null is a valid value
                r.RxSys_DrugID = null;
                r.LabelCode = null;
                r.ProductCode = null;
                r.TradeName = null;
                r.Strength = "0";
                r.Unit = null;
                r.RxOTC = null;
                r.DoseForm = null;
                r.Route =null;
                r.DrugSchedule = 0;
                r.VisualDescription = null;
                r.DrugName = null;
                r.ShortName = null;
                r.NDCNum = null;
                r.SizeFactor = 0;
                r.Template = null;
                r.DefaultIsolate = 0;
                r.ConsultMsg = null;
                r.GenericFor = null;
                r.Write(p);

                // Now use some actual values
                r.RxSys_DrugID = "ABDC12579";
                r.LabelCode = "Mumble";
                r.ProductCode = "1234";
                r.TradeName = "ALPHAFROG@ 120 MG Tablet";
                r.Strength = "12";
                r.Unit = "MG";
                r.RxOTC = "R";
                r.DoseForm = "Tablet";
                r.Route = "Oral";
                r.DrugSchedule = 6;
                r.VisualDescription = "RND/RED/TAB";
                r.DrugName = "ALPHAFROG@ 120 MG";
                r.ShortName = "HAL 120MG";
                r.NDCNum = "00023-0337-01";
                r.SizeFactor = 5;
                r.Template = "C";
                r.DefaultIsolate = 0;
                r.ConsultMsg = "Don't set your hair on fire";
                r.GenericFor = "N/A";
                r.Write(p);

                // Change the record slightly
                r.setField("Action", "Change");
                r.setField("TradeName", "BetaDog");
                r.setField("DrugName", "BETADOG@ 12MG");
                r.setField("ProductCode", null);
                r.setField("GenericFor", null);
                r.Write(p);

                // Delete the ecord
                r.setField("Action", "Delete");
                r.Write(p);
            }
            catch (Exception e)
            {
                Console.Write("testDrug Record Failure {0}", e.Message);
            }
        }

